import styles from "./hotel.module.css"
import Sidebar from "./sidebar"

import { useState } from "react";
import { useEffect } from "react";
const Hotels = () =>
{
  const [selectAll, setSelectAll] = useState(false);
  const [selectedRows, setSelectedRows] = useState([]);
  const [dataHotels, setDataHotels] = useState([])
  useEffect(() =>
  {
    const fetchData = async () =>
    {
      const request = await fetch('http://localhost:5000/dashboard')
      const data = await request.json()

      if (request.ok)
      {
        setDataHotels(data)
      }
    }
    fetchData()
  }, [])


  const handleSelectAll = () =>
  {
    setSelectAll(!selectAll);
    if (!selectAll)
    {
      const allIds = dataHotels.map((hotel, index) => index);
      setSelectedRows(allIds);
    } else
    {
      setSelectedRows([]);
    }
  };
  const handleSelectRow = (id) =>
  {
    if (selectedRows.includes(id))
    {
      setSelectedRows(selectedRows.filter(rowId => rowId !== id));
    } else
    {
      setSelectedRows([...selectedRows, id]);
    }
  };

  return <div className={styles.dashboard}>
    <Sidebar />
    <div className={styles['dashboard-content']}>
      <p>Lastest Transaction</p>
      <div className={styles['dashboard-table']}>
        <table>
          <thead>
            <tr>
              <th>
                <input
                  type="checkbox"
                  checked={selectAll}
                  onChange={handleSelectAll}
                />
              </th>
              <th>ID</th>
              <th>User</th>
              <th>Hotel</th>
              <th>Room</th>
              <th>Date</th>
              <th>Price</th>
              <th>Payment</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {dataTransaction.map((transaction, index) =>
            {
              return <tr key={index}>
                <td>
                  <input
                    type="checkbox"
                    checked={selectedRows.includes(index)}
                    onChange={() => handleSelectRow(index)}
                  />
                </td>
                <td>{transaction.id}</td>
                <td>{transaction.user}</td>
                <td>{transaction.hotel}</td>
                <td>{transaction.room.join(', ')}</td>
                <td>{transaction.date}</td>
                <td>${transaction.price}</td>
                <td>{transaction.payment}</td>
                <td><p>{transaction.status}</p></td>
              </tr>
            })}

            <tr>
              <td></td>
              <td></td>
              <td></td>
              <td></td>
              <td></td>
              <td></td>
              <td></td>
              <td></td>
              <td></td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  </div>


}
export default Hotels
