import Sidebar from "./sidebar"
import './new-room.css'
import { useState } from "react";
const Newroom = () =>
{
  const [selectedRooms, setSelectedRooms] = useState([]);



  const [description, setDescription] = useState("");


  const [maxPeople, setMaxPeople] = useState("");
  const [title, setTitle] = useState("");
  const [price, setPrice] = useState("");
  const [hotel, setHotel] = useState(false);
  const [formMessage, setFormMessage] = useState("");

  const rooms = ['2 Bed Room', '1 Bed Room', 'Budget Twin Room', "Premier City View Room", "Superior basement room", "Basement Double Room", "Budget Double Room", "Deluxe Room", "Deluxe Window"]
  const handleRoomSelect = (selectedRoom) =>
  {
    if (selectedRooms.includes(selectedRoom))
    {
      setSelectedRooms(selectedRooms.filter(room => room !== selectedRoom));
    } else
    {
      setSelectedRooms([...selectedRooms, selectedRoom]);
    }
  };
  const handleSubmit = (e) =>
  {
    e.preventDefault();

    // Kiểm tra xem các trường đã được điền đầy đủ hay không
    if (!description || !maxPeople || !title || !price)
    {
      setFormMessage("Please fill in all fields.");
      return;
    }
    setFormMessage("");
    const sendRequest = async () =>
    {
      const request = await fetch('http://localhost:5000/add-room', {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({


          maxPeople: maxPeople,

        })
      })
      const res = await request.json()
      console.log(res)
      if (request.ok)
      {
        setFormMessage(res.message)
      } else
      {
        setFormMessage(res.error)
      }
    }
    sendRequest()


  };
  return <div className="new-room">
    <Sidebar></Sidebar>
    <div className="new-room_content">
      <h2>Add New Product</h2>
      <form className="new-room_form" onSubmit={handleSubmit}>
        <div className="form-content">
          <div className="form-items">
            <label>Title</label>
            <input type="text" placeholder="The best room" value={title} onChange={(e) => setTitle(e.target.value)} required></input>
            <label>Price</label>
            <input type="text" placeholder="100" value={price} onChange={(e) => setPrice(e.target.value)} required></input>



          </div>
          <div className="form-items">
            <label>Description</label>
            <input type="text" placeholder="description" value={description} onChange={(e) => setDescription(e.target.value)} required></input>
            <label>Max People</label>
            <input type="Number" placeholder="2" value={maxPeople} onChange={(e) => setMaxPeople(e.target.value)} required></input>


          </div>
        </div>
        <div className="rooms">
          <label>Rooms</label>
          <div>
            <p>Featured</p>
            <select defaultValue={hotel} onChange={(e) => setHotel(e.target.value)} required>
              <option value="false" >No</option>
              <option value="true">Yes</option>
            </select>
          </div>
          <button type="submit">Send</button>
        </div>
        {formMessage && <p className="form-error">{formMessage}</p>}
      </form>
    </div>
  </div >
}
export default Newroom
