import Sidebar from "./sidebar"
import './new-room.css'
import { useState } from "react";
const Newroom = () =>
{
  const [selectedRooms, setSelectedRooms] = useState([]);
  const [name, setName] = useState("");
  const [city, setCity] = useState("");
  const [distance, setDistance] = useState("");
  const [description, setDescription] = useState("");
  const [images, setImages] = useState("");
  const [type, setType] = useState("");
  const [address, setAddress] = useState("");
  const [title, setTitle] = useState("");
  const [price, setPrice] = useState("");
  const [featured, setFeatured] = useState(false);
  const [formMessage, setFormMessage] = useState("");

  const rooms = ['2 Bed Room', '1 Bed Room', 'Budget Twin Room', "Premier City View Room", "Superior basement room", "Basement Double Room", "Budget Double Room", "Deluxe Room", "Deluxe Window"]
  const handleRoomSelect = (selectedRoom) =>
  {
    if (selectedRooms.includes(selectedRoom))
    {
      setSelectedRooms(selectedRooms.filter(room => room !== selectedRoom));
    } else
    {
      setSelectedRooms([...selectedRooms, selectedRoom]);
    }
  };
  const handleSubmit = (e) =>
  {
    e.preventDefault();

    // Kiểm tra xem các trường đã được điền đầy đủ hay không
    if (!name || !city || !distance || !description || !images || !type || !address || !title || !price)
    {
      setFormMessage("Please fill in all fields.");
      return;
    }
    setFormMessage("");
    const sendRequest = async () =>
    {
      const request = await fetch('http://localhost:5000/add-room', {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          name: name,
          city: city,
          distance: distance,
          description: description,
          imageUrl: images,
          type: type,
          address: address,
          title: title,
          price: price,
          featured: featured,
          roomTitle: selectedRooms
        })
      })
      const res = await request.json()
      console.log(res)
      if (request.ok)
      {
        setFormMessage(res.message)
      } else
      {
        setFormMessage(res.error)
      }
    }
    sendRequest()


  };
  return <div className="new-room">
    <Sidebar></Sidebar>
    <div className="new-room_content">
      <h2>Add New Product</h2>
      <form className="new-room_form" onSubmit={handleSubmit}>
        <div className="form-content">
          <div className="form-items">
            <label>Title</label>
            <input type="text" placeholder="The best room" value={title} onChange={(e) => setTitle(e.target.value)} required></input>
            <label>Price</label>
            <input type="text" placeholder="100" value={price} onChange={(e) => setPrice(e.target.value)} required></input>



          </div>
          <div className="form-items">
            <label>Description</label>
            <input type="text" placeholder="description" value={description} onChange={(e) => setDescription(e.target.value)} required></input>
            <label>Max People</label>
            <input type="Number" placeholder="elton st. 216" value={address} onChange={(e) => setAddress(e.target.value)} required></input>
            <label>Type</label>
            <input type="text" placeholder="room" value={type} onChange={(e) => setType(e.target.value)} required></input>


            <label>Featured</label>
            <select defaultValue={featured} onChange={(e) => setFeatured(e.target.value === "false" ? false : true)} required>
              <option value="false" >No</option>
              <option value="true">Yes</option>
            </select>
          </div>
        </div>
        <label>Rooms</label>
        {/* <input
          type="text"
          value={roomTitle}
          onChange={(e) => setRoomTitle(e.target.value)}

          hidden
          required
        /> */}
        <ul className="rooms-list">
          {rooms.map((room, index) => (
            <li
              key={index}
              className={selectedRooms.includes(room) ? 'active' : ""}
              onClick={() =>
              {
                handleRoomSelect(room)
                console.log(selectedRooms)
              }
              }>
              {room}
            </li>
          ))}
        </ul>
        <button type="submit">Send</button>
        {formMessage && <p className="form-error">{formMessage}</p>}
      </form>
    </div>
  </div >
}
export default Newroom
