import "./Content.css";
import dataCity from "../../../../data/city.json";
import dataHotel from "../../../../data/hotel_list.json";

const Content = () => {
  return (
    <div className="content">
      {dataCity.map((item, index) => {
        return (
          <div
            key={index}
            className="card"
            style={{ backgroundImage: `url(${item.image})` }}
          >
            <h3>{item.subText}</h3>
            <h2>{item.name}</h2>
          </div>
        );
      })}
    </div>
  );
};
export default Content;
