import { useState } from 'react';
import './FormBooking.css'
import { DateRange } from 'react-date-range'
const FormBooking = ({ dataUser }) =>
{
  const [selectedDateRange, setSelectedDateRange] = useState({
    startDate: new Date(),
    endDate: new Date(),
    key: "selection",
  });
  const [email, setEmail] = useState('')
  const [fullName, setFullName] = useState('')
  const [phoneNumber, setPhoneNumber] = useState('')
  const [cardNumber, setCardNumber] = useState('')
  const handleSelect = (ranges) =>
  {
    setSelectedDateRange(ranges.selection);

  };
  const handleFullName = (e) => { setFullName(e.target.value) }
  const handleEmail = (e) => { setEmail(e.target.value) }
  const handlePhoneNumber = (e) => { setPhoneNumber(e.target.value) }
  const handleCardNumber = (e) => { setCardNumber(e.target.value) }
  return <div className='form-booking'>
    <div className='form-user'>
      <div>
        <h3>Dates</h3>
        <DateRange
          editableDateInputs={true}
          moveRangeOnFirstSelection={false}
          className="date"
          minDate={new Date()}
          onChange={handleSelect}
          ranges={[selectedDateRange]}
        />
      </div>
      <div className='user-info'>
        <h3>Reserve Info</h3>
        <label>Your Full Name:</label>
        <input type='text' placeholder='Full Name' onChange={handleFullName} defaultValue={dataUser.fullName}></input>
        <label>Your Email:</label>
        <input type='email' placeholder='Email' onChange={handleEmail} defaultValue={dataUser.email}></input>
        <label>Your Phone Number:</label>
        <input type='Number' placeholder='Phone Number' onChange={handlePhoneNumber} defaultValue={dataUser.phoneNumber}></input>
        <label >Your Identity Card Number</label>
        <input type='Number' placeholder='Card Number' onChange={handleCardNumber} defaultValue={dataUser.cardNumber}></input>
      </div>
    </div>
    <div>
      <h3>Select Rooms</h3>
      <div></div>
      <div>
        <h3>Total Bill:</h3>
        <div>
          <select value='Select Payment Method'>
            <option value='cash'>Cash</option>
            <option value='card'>Card</option>
            <option value='credit card'>Credit Card</option>
          </select>
          <button className='button'>Reserve Now</button>
        </div>
      </div>
    </div>
  </div>
}
export default FormBooking
