import "./Type.css";
import dataType from "../../../../data/type.json";
const Type = ({ data }) =>
{
  const propertyType = {}
  if (!data)
  {
    return <div>Loading...</div>;
  }
  ["Hotel", "Apartments", "Resorts", "Villas", "Cabins"].forEach(type =>
  {
    propertyType[type] = {
      count: 0,
      image: ""
    }
  });
  data.forEach(item =>
  {
    const type = item.type.charAt(0).toUpperCase() + item.type.slice(1)

    if (propertyType[type])
    {
      propertyType[type]++
    } else
    {
      propertyType[type] = 1
    }
  })
  dataType.forEach((item) =>
  {
    const type = item.name;
    if (propertyType[type])
    {
      propertyType[type].image = item.image;
    }
  });
  console.log(propertyType)
  return (
    <div>
      <h2 className="title">Browse by property type</h2>
      <div className="type">
        {dataType.map((item, index) =>
        {
          return (
            <div key={index} className="card-type">
              <img src={item.image} alt={item.name} />
              <h3>{item.name}</h3>
              <p>{item.count} hotel</p>
            </div>
          );
        })}
      </div>
    </div>
  );
};
export default Type;
