import { useEffect, useState } from "react";
import NavBar from "../home/component/NavBar/NavBar";
import './AuthPage.css'
import { useParams } from "react-router-dom";
const AuthPage = () =>
{
  const { mode } = useParams();
  const [enteredPassword, setEnteredPassword] = useState('')
  const [enteredEmail, setEnteredEmail] = useState('')
  const [heading, setHeading] = useState('');
  const [buttonText, setButtonText] = useState('');

  // Sử dụng useEffect để cập nhật giá trị của heading và buttonText khi mode thay đổi
  useEffect(() =>
  {
    if (mode === 'signup')
    {
      setHeading('Sign Up');
      setButtonText('Create Account');
    } else
    {
      setHeading('Login');
      setButtonText('Login');
    }
  }, [mode]);
  const sendRequest = async (mode) =>
  {
    const url = mode === 'signup' ? 'http://localhost:5000/create-user' : 'http://localhost:5000/login'
    const request = await fetch(url, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ enteredEmail, enteredPassword })
    })
    const res = await request.json()
    if (res.ok)
    {
      if (mode === 'signup')
      {
        console.log(res);
      } else
      {
        console.log(res);
      }
    } else
    {
      console.error('Đăng ký/Đăng nhập thất bại!');
    }

  }
  const handlEmail = (e) =>
  {
    setEnteredEmail(e.target.value)
  }
  const handlPassword = (e) =>
  {
    setEnteredPassword(e.target.value)
  }
  const handlSubmit = (e) =>
  {
    e.preventDefault()
    sendRequest()
  }

  return <div>
    <NavBar ></NavBar>
    <div className="login">
      <h1>{heading}</h1>
      <form className="form-auth">
        <input placeholder="email" type="email" onChange={handlEmail} ></input>
        <input placeholder="password" type="password" onChange={handlPassword} ></input>
        <button onClick={handlSubmit}>{buttonText}</button>
      </form>
    </div>
  </div>
}
export default AuthPage
