import "./Type.css";
import dataType from "../../../../data/type.json";
const Type = ({ data }) =>
{
  const propertyType = {}
  if (!data)
  {
    return <div>Loading...</div>;
  }
  // Khai báo một đối tượng để ánh xạ tên loại tài sản từ cơ sở dữ liệu sang tên bạn muốn hiển thị
  const typeMapping = {
    "hotel": "Hotels",
    "apartment": "Apartments",
    "resort": "Resorts",
    "villa": "Villas",
    "cabin": "Cabins"
  };

  Object.keys(typeMapping).forEach(type =>
  {
    propertyType[typeMapping[type]] = {
      count: 0,
      image: ""
    }
  });

  data.forEach(item =>
  {
    const type = item.type.toLowerCase(); // Chuyển đổi loại tài sản sang chữ thường để so sánh

    if (propertyType[type])
    {
      propertyType[type].count++
    } else
    {
      propertyType[type] = { count: 1, image: '' }
    }
  })

  dataType.forEach((item) =>
  {
    const type = item.name;
    if (propertyType[type])
    {
      propertyType[type].image = item.image;
    }
  });

  console.log(propertyType)
  return (
    <div>
      <h2 className="title">Browse by property type</h2>
      <div className="type">
        {Object.keys(propertyType).map((type, index) =>
        {
          return (
            <div key={index} className="card-type">
              <img src={propertyType[type].image} alt={type} />
              <h3>{type}</h3>
              <p>{propertyType[type].count} {type}</p>
            </div>
          );
        })}
      </div>
    </div>
  );
};
export default Type;
