import "./SearchList.css";
import dataSearch from "../../data/search.json";
import { useSearch } from "../../searchContext/SearchContext"
const SearchList = () =>
{
  const { searchData } = useSearch()
  return (
    <div style={{ display: "flex", flexDirection: "column" }}>
      {dataSearch.map((item, index) => (
        <div key={index} className="card-searchlist">
          <img src={item.image_url} alt={item}></img>
          <div className="detail-searchlist">
            <h3 className="name-hotel">{item.name}</h3>
            <p>{item.distance} from center</p>
            <p className="tag">{item.tag}</p>
            <p className="description">{item.description}</p>
            <p style={{ fontSize: "12px" }}>{item.type}</p>

            {item.free_cancel ? (
              <div>
                <p className="free_cancel">Free cancellation</p>
                <p className="free_cancel-detail">
                  you can cancel later, so lock in this great price today!
                </p>
              </div>
            ) : (
              ""
            )}
          </div>
          <div className="rate-searchlist">
            <div className="rate">
              <p style={{ fontWeight: "bold" }}>{item.rate_text}</p>
              <p style={{ backgroundColor: "#003580", color: "#fff" }}>
                {item.rate}
              </p>
            </div>
            <div className="price">
              <p style={{ fontWeight: "bold", fontSize: "20px" }}>
                ${item.price}
              </p>
              <p style={{ fontSize: "10px" }}>Includes taxes and fees</p>
              <button className="button">See availability</button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};
export default SearchList;
