import "./City.css";

const City = ({ data }) =>
{
  const cityImagePaths = {
    "Ha Noi": "./CityImage/Ha%20Noi.jpg",
    "Da Nang": "./CityImage/Da%20Nang.jpg",
    "Ho Chi Minh": "./CityImage/HCM.jpg"
  };

  const cityCounts = {}

  if (!data)
  {
    return <div>Loading...</div>;
  }
  data.forEach((item) =>
  {
    const city = item.city;
    if (cityCounts[city])
    {
      cityCounts[city]++;
    } else
    {
      cityCounts[city] = 1;
    }

  });
  console.log(cityCounts)

  return (
    <div className="city">
      {Object.keys(cityCounts).map((city, index) =>
      { // Lấy số lượng khách sạn cho thành phố hiện tại
        const hotelCount = cityCounts[city];

        return (
          <div
            key={index}
            className="card-city"
            style={{ backgroundImage: `url(${cityImagePaths[city]})` }}
          >
            <h3>{hotelCount}</h3>
            <h2><span>{city}</span> properties</h2>
          </div>
        );
      })}
    </div>
  );
};
export default City;
