import "./DetailContent.css";
import dataDetail from "../../data/detail.json";
const DetailContent = () => {
  return (
    <div className="detail-content">
      <div className="detail-title">
        <h2>{dataDetail.name}</h2>
        <span>
          <i className="fa-solid fa-location-dot"></i> {dataDetail.address}
        </span>
        <p style={{ color: "blue" }}>{dataDetail.distance}</p>
        <p style={{ color: "green" }}>{dataDetail.price}</p>
      </div>
      <button
        style={{ position: "absolute", top: "3%", right: "3%" }}
        className="button"
      >
        Reserve or Book Now!
      </button>
      <div className="img-detail">
        {dataDetail.photos.map((item, index) => (
          <img key={index} src={item}></img>
        ))}
      </div>
      <div className="detail-apartments">
        <div style={{ width: "75%" }}>
          <h2>{dataDetail.title}</h2>
          <p style={{ fontSize: "12px", lineHeight: "18px" }}>
            {dataDetail.description}
          </p>
        </div>
        <div style={{ width: "20%" }}>
          <p style={{ fontWeight: "bold", color: "#625e6d" }}>
            Perfect for a 9-night stay!
          </p>
          <p>some located</p>
          <div>
            <span style={{ fontWeight: "bold" }}>
              ${dataDetail.nine_night_price}
            </span>
            <span>(9 nights)</span>
          </div>
          <button className="button">Reserve or Book Now!</button>
        </div>
      </div>
    </div>
  );
};
export default DetailContent;
