import { useState } from "react";
import NavBar from "../home/component/NavBar/NavBar";
import './AuthPage.css'
const AuthPage = ({ isLogin }) =>
{
  const [enteredPassword, setEnteredPassword] = useState('')
  const [enteredEmail, setEnteredEmail] = useState('')
  const sendRequest = async () =>
  {
    const request = await fetch('http://localhost:5000/create-user', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ enteredEmail, enteredPassword })
    })
    const res = await request.json()
    if (res.ok)
    {
      // Xử lý khi đăng ký thành công
      console.log('Đăng ký thành công!');
    } else
    {
      // Xử lý khi đăng ký thất bại
      console.error('Đăng ký thất bại!');
    }
  }
  const handlEmail = (e) =>
  {
    setEnteredEmail(e.target.value)
  }
  const handlPassword = (e) =>
  {
    setEnteredPassword(e.target.value)
  }
  const handlSubmit = (e) =>
  {
    e.preventDefault()
    sendRequest()
  }

  return <div>
    <NavBar isLogin={isLogin}></NavBar>
    <div className="login">
      <h1>{isLogin ? 'Login' : 'Sign Up'}</h1>
      <form className="form-auth">
        <input placeholder="email" type="email" onChange={handlEmail} ></input>
        <input placeholder="password" type="password" onChange={handlPassword} ></input>
        <button onClick={handlSubmit}>{isLogin ? 'Login' : 'Create Account'}</button>
      </form>
    </div>
  </div>
}
export default AuthPage
