import "./Content.css";
import dataCity from "../../../../data/city.json";
import dataHotel from "../../../../data/hotel_list.json";

const Content = () => {
  return (
    <div className="content">
      {dataCity.map((item, index) => {
        return (
          <div
            key={index}
            className="card"
            style={{ backgroundImage: `url(${item.url})` }}
          ></div>
        );
      })}
    </div>
  );
};
export default Content;
