import "./SearchPopup.css";
import { useSearch } from "../../searchContext/SearchContext"
import { useState } from "react";
const SearchPopup = () =>
{
  const [isDateRangeVisible, setDateRangeVisible] = useState(false);
  const [selectedDateRange, setSelectedDateRange] = useState({
    startDate: new Date(),
    endDate: new Date(),
    key: "selection",
  });
  const [selectedLocation, setSelectedLocation] = useState("")

  const { searchData } = useSearch()
  const handleLocationChange = (e) =>
  {
    setSelectedLocation(e.target.value)
  }


  return (
    <div className="search-popup">
      <h2>Search</h2>
      <form className="form-booking-information">
        <label>Destination</label>
        <select value={searchData.location} onChange={handleLocationChange}>
          <option value="">Where are you going?</option>
          <option value="Ha Noi">Ha Noi</option>
          <option value="Da Nang">Da Nang</option>
          <option value="Ho Chi Minh">Ho Chi Minh</option>
        </select>
        <label>Check-in Date</label>
        <input
          type="text"
          onClick={() => setDateRangeVisible(true)} // Khi người dùng click vào ô nhập ngày tháng
          value={`${selectedDateRange.startDate.toDateString()}-${selectedDateRange.endDate.toDateString()}`}
          defaultValue={`${searchData.startDate}-${searchData.endDate}`}
          readOnly
        />
        {isDateRangeVisible && ( // Nếu isDateRangeVisible là true, hiển thị modal
          <DateRange
            editableDateInputs={true}
            moveRangeOnFirstSelection={false}
            className="date"
            minDate={new Date()}
            onChange={handleSelect}
            ranges={[selectedDateRange]}
          />
        )}
        <h3>Options</h3>
        <div className="detail-information">
          <label>Min price per night</label>
          <input type="number" min='0'></input>
        </div>
        <div className="detail-information">
          <label>Max price per night</label>
          <input type="number" min='0'></input>
        </div>
        <div className="detail-information">
          <label>Aduit</label>

          <input type="number" min="1" defaultValue={searchData.adults}></input>
        </div>
        <div className="detail-information">
          <label>Children</label>
          <input type="number" min="0" defaultValue={searchData.children}></input>
        </div>
        <div className="detail-information">
          <label>Room</label>
          <input type="number" min="1" defaultValue={searchData.rooms}></input>
        </div>

        <button className="button">Search</button>
      </form>
    </div>
  );
};
export default SearchPopup;
