import dataCity from "../../../../data/city.json";
import "./City.css";

const City = ({ data }) =>
{
  console.log(data)
  return (
    <div className="city">
      {dataCity.map((item, index) =>
      {
        return (
          <div
            key={index}
            className="card-city"
            style={{ backgroundImage: `url(${item.image})` }}
          >
            <h3>{item.subText}</h3>
            <h2>{item.name}</h2>
          </div>
        );
      })}
    </div>
  );
};
export default City;
