import { useState } from "react";
import NavBar from "../home/component/NavBar/NavBar";
import './AuthPage.css'
import { useLocation } from "react-router-dom";
const AuthPage = () =>
{
  const location = useLocation()
  const searchParams = new URLSearchParams(location.search)
  const mode = searchParams.get('mode')


  const [enteredPassword, setEnteredPassword] = useState('')
  const [enteredEmail, setEnteredEmail] = useState('')
  const [isSubmitDisabled, setSubmitDisabled] = useState(true)

  const sendRequest = async (mode) =>
  {// Kiểm tra xem email có đúng định dạng hay không
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const isEmailValid = emailRegex.test(enteredEmail);

    // Kiểm tra xem mật khẩu có đủ độ dài hay không
    const isPasswordValid = enteredPassword.length >= 8;

    // Nếu email và mật khẩu đều hợp lệ, bật nút submit
    setSubmitDisabled(!(isEmailValid && isPasswordValid));

    // Nếu không hợp lệ, không thực hiện yêu cầu gửi đi
    if (!isEmailValid || !isPasswordValid)
    {
      console.error('Invalid email or password');
      return;
    }
    const url = mode === 'signup' ? 'http://localhost:5000/create-user' : 'http://localhost:5000/login';
    console.log(mode);
    const request = await fetch(url, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ email: enteredEmail, password: enteredPassword })
    })

    const res = await request.json()
    if (res.ok)
    {
      if (mode === 'signup')
      {
        console.log(res);
      } else
      {
        localStorage.setItem('userInfo', JSON.stringify({ enteredEmail, enteredPassword }));
        console.log(res);
      }
    } else
    {
      console.log(res);
    }

  }
  const handlEmail = (e) =>
  {
    setEnteredEmail(e.target.value)
  }
  const handlPassword = (e) =>
  {
    setEnteredPassword(e.target.value)
  }
  const handlSubmit = (e) =>
  {
    e.preventDefault()
    sendRequest(mode)
  }

  return <div>
    <NavBar ></NavBar>
    <div className="login">
      <h1>{mode === 'signup' ? 'Sign Up' : 'Login'}</h1>
      <form className="form-auth">
        <input placeholder="email" type="email" onChange={handlEmail} ></input>
        <input placeholder="password" type="password" onChange={handlPassword} ></input>
        <button onClick={handlSubmit}>{mode === 'signup' ? 'Create Account' : 'Login'}</button>
      </form>
    </div>
  </div>
}
export default AuthPage
